def apartmentFloors(instructions):
    floor = 0
    for i in instructions:
        if i == "(":
            floor += 1
        elif i == ")":
            floor -= 1

    return floor


def enterBasement(instructions):
    floor = 0
    for i in range(0,len(instructions)):
        if instructions[i] == "(":
            floor += 1
        elif instructions[i] == ")":
            floor -= 1

        if floor < 0:
            return i+1

    return -1

